import React, { useEffect } from 'react';
import './CustomSlider.scss';
import { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Carousel } from 'react-bootstrap';
import wow1 from '../../images/wow1.jpg'
import wow2 from '../../images/wow2.jpg';
import wow3 from '../../images/wow3.jpg';

function CustomSlider() {

    const sliderRef = useRef();
    const [slider, setSlider] = useState([]);
    const [currentSlide, setCurrentSlide] = useState(0);

    useEffect(() => {
        setSlider([...slider, wow3, wow1, wow2, wow3, wow1]);
    }, [])

    console.log(currentSlide)

    const setSlide = (value) => {

        if (value <= 2 && value >= 0) {
            let prev = value - 1;
            let next = value + 1;
            if (prev < 0) {
                prevSlideRef.current.src = slider[2];
            }
            if (next > 2) {
                nextSlideRef.current.src = slider[0];
            }
        }
        if (value > 2) {
            currentSlideRef.current.src = slider[0];
            setCurrentSlide(0);
            prevSlideRef.current.src = slider[2];
            nextSlideRef.current.src = slider[1];
        }
        else
            if (value < 0) {
                currentSlideRef.current.src = slider[2];
                setCurrentSlide(2);
                prevSlideRef.current.src = slider[1];
                nextSlideRef.current.src = slider[0];
            }
    }

    const leftSlidehandleClick = () => {
        currentSlideRef.current.style.transform = "translateX(920px)";
        prevSlideRef.current.style.transform = "translateX(920px)";
        setSlide(currentSlide - 1);
    }

    const rightSlidehandleClick = () => {
        currentSlideRef.current.style.transform = "translateX(-920px)";
        nextSlideRef.current.style.transform = "translateX(-920px)";
        setSlide(currentSlide + 1);
    }

    return (
        <div className='CustomSlider'>
            <div className='sliderContent'>
                <span className='leftSlide' onClick={leftSlidehandleClick}>
                    <i className="bi bi-arrow-left"></i>
                </span>
                <span className='rightSlide' onClick={rightSlidehandleClick}>
                    <i className="bi bi-arrow-right"></i>
                </span>
                <div className='slider' ref={sliderRef}>
                    <img src={wow3} className="img-fluid" alt="..." ></img>
                    <img src={wow1} className="img-fluid" alt="..." ></img>
                    <img src={wow2} className="img-fluid" alt="..." ></img>
                    <img src={wow3} className="img-fluid" alt="..." ></img>
                    <img src={wow1} className="img-fluid" alt="..." ></img>
                </div>
            </div>
            <div className='indexBx'>

            </div>
        </div>
    )
}

export default CustomSlider
