import React, { useEffect } from 'react';
import './CustomSlider.scss';
import { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Carousel } from 'react-bootstrap';
import wow1 from '../../images/wow1.jpg';
import wow2 from '../../images/wow2.jpg';
import wow3 from '../../images/wow3.jpg';

function CustomSlider() {

    const sliderRef = useRef();
    const [slider, setSlider] = useState([]);
    const [count, setCount] = useState(1);

    useEffect(() => {
        setSlider([...slider, wow3, wow1, wow2, wow3, wow1]);
    }, [])

    console.log(count);

    const leftSlidehandleClick = () => {
        setCount(count - 1);
        sliderRef.current.style.transform = `translate(${(count - 1) * -920}px)`;
        sliderRef.current.style.transition = '0.5s';
    }

    const rightSlidehandleClick = () => {
        setCount(count + 1);
        sliderRef.current.style.transform = `translate(${(count + 1) * -920}px)`;
        sliderRef.current.style.transition = '0.5s';
    }

    const sliderHandleTransitionEnd = () => {
        if (count == 0) {
            setCount(slider.length - 2)
            sliderRef.current.style.transition = '0.1s';
            sliderRef.current.style.transform = `translate(${(slider.length - 2) * -920}px)`;
        }
    }

    return (
        <div className='CustomSlider'>
            <div className='sliderContent'>
                <span className='leftSlide' onClick={leftSlidehandleClick}>
                    <i className="bi bi-arrow-left"></i>
                </span>
                <span className='rightSlide' onClick={rightSlidehandleClick}>
                    <i className="bi bi-arrow-right"></i>
                </span>
                <div className='slider' ref={sliderRef} onTransitionEnd={sliderHandleTransitionEnd}>
                    <img src={wow3} className="img-fluid" alt="..." ></img>
                    <img src={wow1} className="img-fluid" alt="..." ></img>
                    <img src={wow2} className="img-fluid" alt="..." ></img>
                    <img src={wow3} className="img-fluid" alt="..." ></img>
                    <img src={wow1} className="img-fluid" alt="..." ></img>
                </div>
            </div>
            <div className='indexBx'>

            </div>
        </div>
    )
}

export default CustomSlider
