import './CustomSlider.scss';
import { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Carousel } from 'react-bootstrap';
import wow1 from '../../images/wow1.jpg'
import wow2 from '../../images/wow2.jpg';
import wow3 from '../../images/wow3.jpg';

function CustomSlider() {

    const prevSlideRef = useRef();
    const currentSlideRef = useRef();
    const nextSlideRef = useRef();

    const leftSlidehandleClick = () => {
        currentSlideRef.current.style.transform = "translateX(-920px)"
    }

    const rightSlidehandleClick = () => {

    }

    return (
        <div className='CustomSlider'>
            <div className='sliderContent'>
                <span className='leftSlide'>
                    <i className="bi bi-arrow-left" onClick={leftSlidehandleClick}></i>
                </span>
                <span className='rightSlide'>
                    <i className="bi bi-arrow-right" onClick={rightSlidehandleClick}></i>
                </span>
                <div className='slider'>
                    <img src={wow1} className="img-fluid" alt="..." ref={prevSlideRef}></img>
                    <img src={wow2} className="img-fluid" alt="..." ref={currentSlideRef}></img>
                    <img src={wow3} className="img-fluid" alt="..." ref={nextSlideRef}></img>
                </div>
            </div>
            <div className='indexBx'>

            </div>
        </div>
    )
}

export default CustomSlider
